import React from 'react';
import Button from '../Button';
import imgDefault from '../../assets/NoCard.svg';
import Card from '../Card';
import './style.css';

const List = ({ listTransactions, setListTransactions }) => {
  const handleClick = (e) => {
    e.target.innerText === 'Todos' ? setListTransactions([...listTransactions]) : ''

  };

  return (
    <section className="section">
      <div className="section__summary">
        <h3>Resumo financeiro</h3>
        <div className="summary__btns">
          <Button
            text="Todos"
            btnClass="btn-default"
            handleClick={handleClick}
          />
          <Button
            text="Entradas"
            btnClass="btn-default btn-grey"
            handleClick={handleClick}
          />
          <Button
            text="Despesas"
            btnClass="btn-default btn-grey"
            handleClick={handleClick}
          />
        </div>
      </div>

      {!listTransactions.length && (
        <h2>Você ainda não possui nenhum lançamento</h2>
      )}

      <ul>
        {listTransactions.length ? (
          listTransactions.map((elem, index) => (
            <Card
              listTransactions={listTransactions}
              key={index}
              description={elem.description}
              type={elem.type}
              value={elem.value}
              cardClass={elem.type === 'Entrada' ? 'card entry' : 'card'}
            />
          ))
        ) : (
          <>
            <img src={imgDefault} alt="No card" />
            <img src={imgDefault} alt="No card" />
            <img src={imgDefault} alt="No card" />
          </>
        )}
      </ul>
    </section>
  );
};

export default List;
