import React from 'react';
import './style.css';
import trash from '../../assets/trash.svg';

const Card = ({ description, type, value, cardClass }) => {
  return (
    <ul>
      <li className={cardClass}>
        <div className="card__info">
          <h3>{description}</h3>
          <div className="info__div">
            <span>R$ {value}</span>
            <button type="button">
              <img src={trash} alt="Lixeira" />
            </button>
          </div>
        </div>
        <span>{type}</span>
      </li>
    </ul>
  );
};

export default Card;
