import Button from '../Button';
import imgDefault from '../../assets/NoCard.svg';
import Card from '../Card';
import './style.css';

const List = ({ listTransactions }) => {
  return (
    <section className="section">
      <div className="section__summary">
        <h3>Resumo financeiro</h3>
        <div className="summary__btns">
          <Button text="Todos" btnClass="btn-default" />
          <Button text="Entradas" btnClass="btn-default btn-grey" />
          <Button text="Despesas" btnClass="btn-default btn-grey" />
        </div>
      </div>

      <h2>Você ainda não possui nenhum lançamento</h2>

      {listTransactions.length ? (
        listTransactions.map((elem) => (
          <Card
          key={index}
            description={elem.description}
            type={elem.type}
            value={elem.value}
            cardClass={elem.type === 'Entrada' ? 'card entry' : 'card'}
          />
        ))
      ) : (
        <>
          <img src={imgDefault} alt="No card" />
          <img src={imgDefault} alt="No card" />
          <img src={imgDefault} alt="No card" />
        </>
      )}
    </section>
  );
};

export default List;
