import './style.css';
import Button from '../Button';
import Card from '../Card';
import ImageDefault from '../ImageDefault';
import React, { useState } from 'react';

const List = ({ listTransactions, setListTransactions }) => {
  const removeItem = (item) => {
    setListTransactions((list) => list.filter((elem) => elem !== item));
  };

  const [itemFilter, setItemFilter] = useState('Todos');
  const [listFilter, setListFilter] = useState([]);

  const check = (target, value) => {
    setItemFilter(value);

    if (target === 'Entrada') {
      const itemFilter = listTransactions.filter(
        (elem) => elem.type === target,
      );
      setListFilter(itemFilter);
    } else {
      const itemFilter = listTransactions.filter(
        (elem) => elem.type === target,
      );
      setListFilter(itemFilter);
    }
  };

  return (
    <section className="section">
      <div className="section__summary">
        <h3>Resumo financeiro</h3>
        <div className="summary__btns">
          <Button
            text="Todos"
            btnClass={
              itemFilter === 'Todos' ? 'btn-default' : 'btn-default btn-grey'
            }
            handleClick={() => check('Todos')}
          />
          <Button
            text="Entradas"
            btnClass={
              itemFilter === 'Entrada' ? 'btn-default' : 'btn-default btn-grey'
            }
            handleClick={() => {
              check('Entrada');
              console.log(listFilter);
            }}
          />
          <Button
            text="Despesas"
            btnClass={
              itemFilter === 'Despesa' ? 'btn-default' : 'btn-default btn-grey'
            }
            handleClick={() => {
              check('Despesa');
              // console.log(listFilter);
            }}
          />
        </div>
      </div>
      {!listTransactions.length && (
        <h2>Você ainda não possui nenhum lançamento</h2>
      )}
      <ul>
        {itemFilter === 'Todos' && listTransactions.length ? (
          listTransactions.map((elem, index) => (
            <Card
              removeItem={removeItem}
              key={index}
              transaction={elem}
              cardClass={elem.type === 'Entrada' ? 'card entry' : 'card'}
            />
          ))
        ) : (
          <ImageDefault />
        )}
      </ul>
    </section>
  );
};

export default List;
