import React from 'react';
import Button from '../Button';
import imgDefault from '../../assets/NoCard.svg';
import Card from '../Card';
import './style.css';
import { useState } from 'react';

const List = ({ listTransactions, setListTransactions }) => {
  const removeItem = (item) => {
    setListTransactions((elem) => elem.filter(elem));
  };

  const renderAll = () => {
    setListTransactions([...listTransactions]);
  };

  const [filterItem, setFilterItem] = useState([]);

  const renderEntry = () => {
    const filter = listTransactions.filter((elem) => elem.type === 'Entrada');
    setFilterItem(filter);
  };

  const renderOut = () => {
    const filter = listTransactions.filter((elem) => elem.type === 'Despesa');
    setFilterItem(filter);
  };

  return (
    <section className="section">
      <div className="section__summary">
        <h3>Resumo financeiro</h3>
        <div className="summary__btns">
          <Button text="Todos" btnClass="btn-default" handleClick={renderAll} />
          <Button
            text="Entradas"
            btnClass="btn-default btn-grey"
            handleClick={renderEntry}
          />
          <Button
            text="Despesas"
            btnClass="btn-default btn-grey"
            handleClick={renderOut}
          />
        </div>
      </div>

      {!listTransactions.length && (
        <h2>Você ainda não possui nenhum lançamento</h2>
      )}

      <ul>
        {listTransactions.length ? (
          listTransactions.map((elem, index) => (
            <Card
              removeItem={removeItem}
              listTransactions={listTransactions}
              key={index}
              description={elem.description}
              type={elem.type}
              value={elem.value}
              cardClass={elem.type === 'Entrada' ? 'card entry' : 'card'}
            />
          ))
        ) : (
          <>
            <img src={imgDefault} alt="No card" />
            <img src={imgDefault} alt="No card" />
            <img src={imgDefault} alt="No card" />
          </>
        )}
      </ul>
    </section>
  );
};

export default List;
