import Button from '../Button';
import imgDefault from '../../assets/NoCard.svg';
import Card from '../Card';
import './style.css';
import { useState } from 'react';
import FlatList from 'flatlist-react';

const List = ({ listTransactions, setListTransactions }) => {
  const removeItem = (item) => {
    setListTransactions((list) => list.filter((elem) => elem !== item));
  };

  const [itemFilter, setItemFilter] = useState('All');

  const check = (value) => setItemFilter(value);

  return (
    <section className="section">
      <div className="section__summary">
        <h3>Resumo financeiro</h3>
        <div className="summary__btns">
          <Button
            text="Todos"
            btnClass="btn-default"
            onClick={() => check('All')}
          />
          <Button
            text="Entradas"
            btnClass="btn-default btn-grey"
            onClick={() => check('Entry')}
          />
          <Button
            text="Despesas"
            btnClass="btn-default btn-grey"
            onClick={() => check('Out')}
          />
        </div>
      </div>
      {!listTransactions.length && (
        <h2>Você ainda não possui nenhum lançamento</h2>
