import React from 'react';
import './style.css';
import trash from '../../assets/trash.svg';

const Card = ({
  listTransactions,
  setListTransactions,
  description,
  type,
  value,
  cardClass,
}) => {
  const deleteItem = (e) => {
    console.log(e.target.closest('l'));
    // listTransactions.find();
  };

  return (
    <li className={cardClass}>
      <div className="card__info">
        <h3>{description}</h3>
        <div className="info__div">
          <span>R$ {value}</span>
          <button type="button" onClick={deleteItem}>
            <img src={trash} alt="Lixeira" />
          </button>
        </div>
      </div>
      <span>{type}</span>
    </li>
  );
};

export default Card;
