import React from 'react';
import './style.css';
import Button from '../Button';
import { useState } from 'react';

const Form = ({ listTransactions, setListTransactions }) => {
  const [description, setDescription] = useState('');
  const [value, setValue] = useState("");
  const [entrance, setEntrance] = useState("Entrada");

  const handleSubmit = (e) => {
    e.preventDefault();
    setListTransactions([
      ...listTransactions,
      {
        description: e.target.description.value,
        type: e.target.typeValue.value,
        value:
          e.target.typeValue.value === 'Despesa'
            ? Number(e.target.valueInfo.value * -1)
            : Number(e.target.valueInfo.value),
      },
    ]);

    e.target.description.value = '';
    e.target.valueInfo.value = '';
    e.target.typeValue.value = 'Entrada';
  };

  return (
    <form className="form" onSubmit={handleSubmit}>
      <div className="form__description">
        <label htmlFor="description">Descrição</label>
        <input
          type="text"
          id="description"
          placeholder="Digite aqui sua descrição"
          required
        />
        <span>Ex: Compra de roupas</span>
      </div>
      <div className="form__infos">
        <div className="info__value">
          <label htmlFor="valueInfo">Valor</label>
          <input type="number" id="valueInfo" placeholder="1" required />
          <span>R$</span>
        </div>
        <div>
          <label htmlFor="typeValue">Tipo de valor</label>
          <div className="select">
            <select id="typeValue" required>
              <option value="Entrada">Entrada</option>
              <option value="Despesa">Despesa</option>
            </select>
          </div>
        </div>
      </div>
      <Button type="submit" text="Inserir valor" btnClass="btn-default" />
    </form>
  );
};

export default Form;
