import './style.css';
import Button from '../Button';
import Card from '../Card';
import imgDefault from '../../assets/NoCard.svg';
import React, { useState } from 'react';

const List = ({ listTransactions, setListTransactions }) => {
  const removeItem = (item) => {
    setListTransactions((list) => list.filter((elem) => elem !== item));
  };

  const [itemFilter, setItemFilter] = useState('All');
  const check = (value) => setItemFilter(value);

  return (
    <section className="section">
      <div className="section__summary">
        <h3>Resumo financeiro</h3>
        <div className="summary__btns">
          <Button
            text="Todos"
            btnClass={
              itemFilter === 'All' ? 'btn-default' : 'btn-default btn-grey'
            }
            handleClick={() => check('All')}
          />
          <Button
            text="Entradas"
            btnClass={
              itemFilter === 'Entry' ? 'btn-default' : 'btn-default btn-grey'
            }
            handleClick={() => check('Entry')}
          />
          <Button
            text="Despesas"
            btnClass={
              itemFilter === 'Out' ? 'btn-default' : 'btn-default btn-grey'
            }
            handleClick={() => check('Out')}
          />
        </div>
      </div>
      {!listTransactions.length && (
        <h2>Você ainda não possui nenhum lançamento</h2>
      )}
      <ul>
        {itemFilter === 'All' && list ? (
          listTransactions.map((elem, index) => (
            <Card
              removeItem={removeItem}
              listTransactions={listTransactions}
              key={index}
              description={elem.description}
              type={elem.type}
              value={elem.value}
              cardClass={elem.type === 'Entrada' ? 'card entry' : 'card'}
            />
          ))
        ) : (
          <>
            <img src={imgDefault} alt="No card" />
            <img src={imgDefault} alt="No card" />
            <img src={imgDefault} alt="No card" />
          </>
        )}

        {/* {listTransactions.length ? (
          listTransactions.map((elem, index) => (
            <Card
              removeItem={removeItem}
              listTransactions={listTransactions}
              key={index}
              description={elem.description}
              type={elem.type}
              value={elem.value}
              cardClass={elem.type === 'Entrada' ? 'card entry' : 'card'}
            />
          ))
        ) : (
          <>
            <img src={imgDefault} alt="No card" />
            <img src={imgDefault} alt="No card" />
            <img src={imgDefault} alt="No card" />
          </>
        )} */}
      </ul>
    </section>
  );
};

export default List;
