import React from 'react';
import './style.css';
import Button from '../Button';

const Form = ({ listTransactions, setListTransactions }) => {
  const handleSubmit = () => {
    setListTransactions([...listTransactions, {}])
  };

  return (
    <form className="form" onSubmit={(e) => handleSubmit(e.preventDefault())}>
      <div className="form__description">
        <label htmlFor="description">Descrição</label>
        <input
          onInput={(e) => console.log(e.target.value)}
          type="text"
          id="description"
          placeholder="Digite aqui sua descrição"
        />
        <span>Ex: Compra de roupas</span>
      </div>
      <div className="form__infos">
        <div className="info__value">
          <label htmlFor="value">Valor</label>
          <input type="text" id="value" placeholder="1" />
          <span>R$</span>
        </div>
        <div>
          <label htmlFor="typeValue">Tipo de valor</label>
          <div className="select">
            <select id="typeValue">
              <option value="entry">Entrada</option>
              <option value="out">Despesas</option>
            </select>
          </div>
        </div>
      </div>
      <Button type="submit" text="Inserir valor" btnClass="btn-default" />
    </form>
  );
};

export default Form;
